import { ref, onMounted } from 'vue';
import axios from 'axios';
export default {
  name: 'App',
  setup() {
    const navigation = ref([]);
    const footer = ref({
      copyright: '',
      links: []
    });
    const fetchSettings = async () => {
      try {
        const response = await axios.get('/api/settings.php');
        if (response.data) {
          navigation.value = response.data.navigation || [];
          footer.value = response.data.footer || {
            copyright: '',
            links: []
          };
        }
      } catch (error) {
        console.error('Error fetching settings:', error);
      }
    };
    onMounted(() => {
      fetchSettings();
    });
    return {
      navigation,
      footer
    };
  }
};
